// Demo Dioxus application using dioxus-iconify generated icons
//
// To generate icons for this demo, run:
//   cargo run -- --output examples/demo-app/src/icons add mdi:home mdi:account heroicons:arrow-left

use dioxus::prelude::*;

mod icons;
use crate::icons::*;

fn main() {
    dioxus::launch(App);
}

#[component]
fn App() -> Element {
    rsx! {
        div {
            style: "font-family: sans-serif; padding: 2rem;",

            h1 { "üé® Dioxus Iconify Demo" }

            p {
                "This demo shows how to use icons generated by "
                code { "dioxus-iconify" }
                "."
            }
            div {
                style: "margin-top: 2rem;",

                h2 { "üñºÔ∏è Icon Examples" }

                div {
                    style: "display: flex; gap: 2rem; align-items: center; margin-top: 1rem;",

                    div {
                        h3 { "Home (default)" }
                        Icon { data: mdi::Home }
                    }

                    div {
                        h3 { "Home (large, red)" }
                        Icon {
                            data: mdi::Home,
                            width: "48",
                            height: "48",
                            fill: "red",
                        }
                    }

                    div {
                        h3 { "Account (custom)" }
                        Icon {
                            data: mdi::Account,
                            width: "32",
                            height: "32",
                            fill: "blue",
                        }
                    }

                    div {
                        h3 { "Arrow Left" }
                        Icon {
                            data: heroicons::ArrowLeft,
                            width: "24",
                            height: "24",
                        }
                    }
                }
            }
        }
    }
}
